import styled from "styled-components";

export const MainLayout = styled.div`
    padding: 1rem;
    height: 100%;
    width: 100%;
    display: flex;
    gap: 0.5rem;
    align-items:center;
    margin:auto;
    
`;

export const InnerLayout = styled.div`
    padding: 0.5rem 0.5rem;
    width: 100%;
`;
